from datalab_sdk.api.exceptions import InputNotValid, ResourceNotFound

class LogNotFound(ResourceNotFound):
    """Raised when the logs are not found in DataLab"""
    def __init__(self, tool_id, tool_version_id, correlation_id: str = None):
        super().__init__(f"The requested logs for tool id {tool_id} and tool version {tool_version_id} were not found", correlation_id)