from marshmallow_dataclass import dataclass
from datalab_sdk.api.common.schema import CamelCaseSchema, LowerCamelCaseSchema, CamelCaseSchemaWithCapitalId
from typing import List,TypedDict

@dataclass(base_schema=CamelCaseSchema)
class IsLockedRequest:
    is_locked: bool
    
@dataclass(base_schema=CamelCaseSchema)  
class IsObsoleteRequest:
    is_obsolete: bool

@dataclass(base_schema=CamelCaseSchema)  
class TerminationDateRequest:
    termination_date: int

@dataclass(base_schema=CamelCaseSchema)  
class TagsRequest:
    to_add: List[str]
    to_remove: List[str]

@dataclass(base_schema=CamelCaseSchemaWithCapitalId)  
class IndividualAccess:
    expires_timestamp: int
    user_object_id: str

@dataclass(base_schema=CamelCaseSchema) 
class IndividualAccessRequest:
    individual_access: List[IndividualAccess]
    
@dataclass(base_schema=LowerCamelCaseSchema) 
class BulkAPIResponse:
    message:str

@dataclass(base_schema=CamelCaseSchema) 
class AccessGroupRequest:
    access_groups:List[str]