
from datalab_sdk.api.exceptions import InputNotValid, ResourceNotFound

class ChainAssemblyFailed(InputNotValid):
    """Raised when the chain assembly attempt failed for any reason"""
    def __init__(self, correlation_id: str = None):
            super().__init__(f"The attempted chain assembly failed", correlation_id)

class ChainRunNotFound(ResourceNotFound):
    """Raised when the specified chain run was not found by MME"""
    def __init__(self, chain_run_id: str, correlation_id: str = None):
            super().__init__(f"The requested chain run ({chain_run_id}) was not found in MME", correlation_id)
