from marshmallow_dataclass import dataclass
from datalab_sdk.api.common.schema import CamelCaseSchema
from typing import List, Dict
from datalab_sdk.api.mme.types.responses.input_definition import InputDefinition
from datalab_sdk.api.mme.types.responses.output_definition import OutputDefinition
from datalab_sdk.api.chaining.types.shared.chain_model_reference import ChainModelReference


@dataclass(base_schema=CamelCaseSchema)
class ChainDefinition:
    chain_model_references: List[ChainModelReference]
    input_definitions: List[InputDefinition]
    output_definitions: List[OutputDefinition]
    adjacency_list: Dict[str, List[str]]
