from marshmallow_dataclass import dataclass
from datalab_sdk.api.common.schema import CamelCaseSchema
from typing import List
from datalab_sdk.api.mme.types.requests.business_context import BusinessContext
from uuid import uuid4

@dataclass(base_schema=CamelCaseSchema)
class Correlation:
    business_context: List[BusinessContext]
    correlation_id: str = str(uuid4())
