from marshmallow_dataclass import dataclass
from dataclasses import field
from datalab_sdk.api.common.schema import CamelCaseSchema
from datalab_sdk.api.mme.types.shared.enums import ModelState
from datalab_sdk.api.mme.types.requests.model_reference import FixedModelReference, ModelReference
from typing import List, Union

@dataclass(base_schema=CamelCaseSchema)
class ResolveModelRequest:
    models_to_check: List[ModelReference]
    min_state: ModelState = field(metadata=dict(by_value=True))
    compatible_time_point: Union[str, None] 
