from marshmallow_dataclass import dataclass
from dataclasses import field
from datalab_sdk.api.mme.types.responses.container_definition import ContainerDefinition
from datalab_sdk.api.mme.types.shared.unit import Unit
from typing import Union
from datalab_sdk.api.common.schema import CamelCaseSchema
from datalab_sdk.api.mme.types.shared.enums import DataType


@dataclass(base_schema=CamelCaseSchema)
class DataDefinition:
    data_type: DataType = field(metadata=dict(by_value=True))
    container_definition: ContainerDefinition = field(metadata=dict(by_value=True))
    expected_unit: Union[Unit, None] = None
