from dataclasses import field
from marshmallow_dataclass import dataclass
from typing import List, Union
from datalab_sdk.api.mme.types.shared.list_value import ListValue
from datalab_sdk.api.mme.types.responses.validation_rule import ValidationRule
from datalab_sdk.api.common.schema import CamelCaseSchema


@dataclass(base_schema=CamelCaseSchema)
class DisplayDefinition:
    label: Union[str, None]
    help_text: Union[str, None] = None
    validation_rule: Union[ValidationRule, None] = None
    list_values: List[ListValue] = field(default_factory=list)

