
from marshmallow_dataclass import dataclass
from typing import List
from dataclasses import field
from datalab_sdk.api.common.schema import CamelCaseSchema
from datalab_sdk.api.mme.types.responses.model_key import ModelKey
from datalab_sdk.api.mme.types.shared.enums import ClassTwoRunStatus

@dataclass(base_schema=CamelCaseSchema)
class LogMessage:
    message: str
    time_stamp: str

@dataclass(base_schema=CamelCaseSchema)
class ModelRunProgress:
    run_id: str
    submitted: str
    status:ClassTwoRunStatus = field(metadata=dict(by_value=True))
    progress: List[LogMessage]
    errors: List[LogMessage]
    warnings: List[LogMessage]
    info: List[LogMessage]
    execution_elapsed_time_seconds: int


