from marshmallow_dataclass import dataclass
from typing import Union
from datalab_sdk.api.common.schema import CamelCaseSchema


@dataclass(base_schema=CamelCaseSchema)
class ComponentIngredient:
    name: str
    percent_active: Union[float, int]
    percent_inclusion: Union[float, int]

    def __str__(self):
        return f"{self.name}({self.percent_active}:{self.percent_inclusion})"

