class ClassTwoRunStatus(Enum):
    NULL_RUN_STATUS = "Unknown"
    PENDING = "Pending"
    RUNNING = "Running"
    ENDED = "Ended"

class ModelState(Enum):
    NULL_MODEL_STATE = "Unknown"
    CREATED = "Created"
    PUBLISHED = "Published"
    RECOMMENDED = "Recommended"
    TRUSTED = "Trusted"
    RETIRED = "Retired"
    ARCHIVED = "Archived"


class ModelClass(Enum):
    NULL_MODEL_CLASS = "Unknown"
    CLASS_ONE = "ClassOne"
    CLASS_TWO = "ClassTwo"
