from marshmallow import ValidationError
from marshmallow_dataclass import dataclass, class_schema, Optional
from typing import Any, List
from datalab_sdk.api.common.schema import LowerCamelCaseSchema

@dataclass(base_schema=LowerCamelCaseSchema)
class Substance:
    backbone_identity: str
    sir_identity: str
    type: str

@dataclass(base_schema=LowerCamelCaseSchema)
class Substances:
    substances: List[Substance]

@dataclass(base_schema=LowerCamelCaseSchema)
class SubstancesResponseItem:
    search_item: str
    results: List[Substance]

@dataclass(base_schema=LowerCamelCaseSchema)
class SubstancesResponse:
    content: List[SubstancesResponseItem]

@dataclass(base_schema=LowerCamelCaseSchema)
class SirGetResponse:
    content: List[Any]


