from marshmallow_dataclass import dataclass
from dataclasses import field
from datalab_sdk.api.common.schema import CamelCaseSchema
from datalab_sdk.api.tool_inventory.types.shared.enums import ToolVersionStatus


@dataclass(base_schema=CamelCaseSchema)
class ToolVersion:
    id: str
    version: str
    location: str
    status: ToolVersionStatus = field(metadata=dict(by_value=True))
    configuration: str
