from datalab_sdk.api.tool_session.types.basic_metadata import *
from datalab_sdk.api.platform.basic import KeyValueField

@dataclass
class ExpandedSessionContext():
    session_id: str
    name: str
    tool_version: str
    configuration: str
    inputs: List[SessionInput]
    status: str
    user: str
    tool_viewer_access_groups: List[str]

@dataclass
class SnapshotOutputFile():
    name: str
    type: str
    data: bytes
    file_name: str
    tags: List[str]
    key_value_fields: List[KeyValueField]
    version_id: str
    enable_mme_access: bool = False
